INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090', 'Uganda', 'Charles Trotter collection', 'Charles Trotter was born in Scotland in 1923, but at the age of three months he and his mother Margaret returned home to Uganda, where his father James worked in the Land and Surveys Department. With a break for war service, James had been with the same department in Uganda since graduating from Cambridge in 1912. Margaret (nee Duncan) had gone out to Kenya as a Post Office clerk in 1918, and was later transferred to Entebbe, where she met James. They were married in 1921.
