INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/51', 'Kenya', 'Duke of Edinburgh with Officers of the Kenya Regiment', 'A group photograph showing the Duke of Edinburgh, who wears a hat, standing in the middle of a group of four army officers.  In the background stands a mix of onlookers and African army guards.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '114869', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/52', 'Kenya', 'Duke of Edinburgh with Officers of the Kenya Regiment', 'Four army officers stand to attention in front of the Duke of Edinburgh who is standing with The Govenor of Kenya, Sir Phillip Mitchell and a second, unidentified officer in white uniform.  Around them are gathered a large crowd of onlookers, many of them holding cameras.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '114874', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/53', 'Kenya', 'Nairobi Cathedral', 'Photograph showing Nairobi Cathedral surrounded by trees on either side and grass to the foreground. Possibly a back or side elevation.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '121443', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/54', 'Kenya', 'St Andrew's Church, Nairobi', 'Front elevation of St Andrew's Church, built to a contemporary design, a former constituent of the Overseas Presbytery of the Church of Scotland until the formation of the Presbyterian Church of East Africa in 1956. A small path leading up to the entrance is bordered by two Union Jack flags.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '115383', 2001/090/1);
