INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/57', 'Kenya', 'Princess Elizabeth is driven past onlookers', 'An open topped car speeds past the camera carrying Princess Elizabeth and the Duke of Edinburgh to Nairobi's City Hall.  The Duke of Edinburgh is partially obscured by the window of the car but can be seen waving his left hand.  Princess Elizabeth is seen smiling to the crowds of people who line the road.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '116244', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/58', 'Kenya', 'Nairobi City Hall', 'Front and side elevations of Nairobi's City Hall. Outside the building are a line of flags bearing the Union Jack together with bunting and pennants depicting an ostrich with the words "CITY OF NAIROBI.''  One flag bears the cross of St. Andrew.  Workmen can be seen erecting a flag at the front of the City Hall. and cars line the street outside.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '120075', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/59', 'Kenya', 'default', 'default', '1952-02-01', '1952-02-28', 'Feb-52', 'default', '1 photograph', '1 b/w photograph, glued into album.', 'default', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/6', 'Kenya', 'African Chiefs await the Royal party at Eastleigh Airport', 'Photograph depicting five elderly chiefs sitting at the front of a spectator crowd awaiting the arrival of the Royal party.  The man sitting closest to camera wears a head scarf and holds a staff bearing the Royal insignia.  Two other men also wear this head dress.  The men sitting in the middle of the front row wear feathered plumes and leopard skin neck ruffs.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '121111', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/60', 'Kenya', 'default', 'default', '1952-02-01', '1952-02-28', 'Feb-52', 'default', '1 photograph', '1 b/w photograph, glued into album.', 'default', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/61', 'Kenya', 'Mayor and Mayoress of Nairobi await the Royal party', 'Standing on the front steps of Nairobi City Hall are the Mayor and Mayoress of Nairobi.  The mayor wears a chain of office and holds a hat in his left hand.  Above them can be seen part of the motto "CONSILIO FIDE VIGILANTIA."', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '114713', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/62', 'Kenya', 'default', 'default', '1952-02-01', '1952-02-28', 'Feb-52', 'default', '1 photograph', '1 b/w photograph, glued into album.', 'default', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/63', 'Kenya', 'On the balcony of Nairobi City Hall', 'From the balcony of Nairobi City Hall, the Princess and the Duke wave to the crowds.  They stand between two of the building's four frontal pillars and below them is a large pennant with two lions either side of a coat of arms.  Below the coat of arms is the motto "CONSILIO FIDE VIGILANTIA.''  In front of the building flies a flag bearing the Royal crest.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '116249', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/64', 'Kenya', 'Close up of Princess and Duke on the balcony', 'Detail from previous photograph (2001/090/1/4/1/63, negative number 1319) showing a close-up image of the Princess and the Duke waving to the crowds from the balcony of Nairobi City Hall.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '116250', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/65', 'Kenya', 'Royal party leaving City Hall', 'Princess Elizabeth and the Duke of Edinburgh, together with the Mayor and Mayoress of Nairobi, stand on the steps of City Hall as they get ready to leave.  The Princess waves as the whole group looks to its right.  In front of the group stands a mace bearer wearing a military uniform.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '114596', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/66', 'Kenya', 'New Stanley Hotel, Nairobi', 'Photograph showing the front elevation of the New Stanley Hotel in Nairobi, shortly before its 1950s rebuilding.  Outside of the hotel is a busy street scene, with bunting and a Union Jack flag.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '117281', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/67', 'Kenya', 'default', 'default', '1952-02-01', '1952-02-28', 'Feb-52', 'default', '1 photograph', '1 b/w photograph, glued into album.', 'default', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/68', 'Kenya', 'Sagana Lodge, Nyeri', 'View across gardens of Sagana Lodge, the "Royal Lodge" presented to Princess Elizabeth who according to Trotter's caption left Kenya without visiting it, although she is believed to in fact have spent one night there. The photograph shows a ground floor timber building with a tiled roof set amongst trees and copious flower boundaries.  A gravel circular drive curves in front of the building.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '114585', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/69', 'Kenya', 'default', 'default', '1952-02-01', '1952-02-28', 'Feb-52', 'default', '1 photograph', '1 b/w photograph, glued into album.', 'default', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/7', 'Kenya', 'African chiefs wait behind guard, Eastleigh Airport', 'A crowd of African male spectators with front most spectators wearing tribal dress sit behind a rope line waiting for the arrival of the Royal party.  In front of this stands an official guard who, standing to attention, wears a fez.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '121112', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/70', 'Kenya', 'Sagana Lodge and grounds', 'Rear elevation of Sagana Lodge, a ground floor timber building on a stone basement with a tiled roof set amongst trees and copious flower boundaries.  In the foreground is a garden with unidentified plants growing in it.  A secondary, smaller building is set back from the main lodge.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '114587', 2001/090/1);
