INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/73', 'Kenya', 'River running through the grounds of Sagana Lodge', 'View down from a grassy bank onto a river running through the grounds of the Sagana (Royal) Lodge.  On the right hand side it is bordered by a small path and a more indistinguishable track on the other side of the river.  Lots of trees surround.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '115865', 2001/090/1);
