INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/1/9', 'Kenya', 'Sir Phillip Mitchell with African Chiefs', 'Photograph depicting the Govenor of Kenya, Sir Phillip Mitchell looking down into the camera at Eastleigh Airport.  He wears spectacles, has a white coloured suit with a sash and a row of medals on the left  of his chest.  On his right are standing a group of elderly African chiefs.', '1952-02-01', '1952-02-28', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', '115509', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/4/3', 'Kenya', 'The Queen Mother's Visit to East Africa', 'This album, compiled by Trotter, contains 22 pictures of H.M. Queen Elizabeth the Queen Mother's visit to East Africa in February 1959. Trotter took a huge number of photographs during this visit, and this selection represents a very small number of the images available in negative form. As with the photographs of Princess Elizabeth and Prince Philip in 2001/090/1/4/1, it is clear that Trotter was allowed considerable access during the visit, and took many photographs at close quarters.
