INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/5/1/42', 'Kenya', 'Maasai men with Trotter's camera', 'Two African men in Amboseli National Park, believed to be Maasai, peer into the viewfinder of a large format single lens reflex Graflex camera that they hold between them. They are dressed in traditional costume and jewellery and their braided hair is tied back and daubed with ochre.
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/7', 'Kenya', 'Film', 'Charles Trotter carried out the camera work on this 1953 documentary about the Mau Mau insurgency. It is not known whether there are other Trotter films in existence.', '1953-01-01', '1953-12-31', '1953', 'default', 'default', 'default', 'default', 2001/090/1);
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/1/7/1', 'Kenya', 'Mau Mau', 'Produced in the US by Rock-Price Productions, with camera work by Charles Trotter and narrated by Chet Huntley, this documentary covers the 1953 Mau Mau uprising which originated amongst the Kikuyu people in Kenya. Alongside footage of actual events, such as Sir Evelyn Baring's visit to Kikuyu country, are a number of rather amateur reconstructions, including courtroom scenes and Mau Mau ceremonies. The narration and music appear dramatised for effect, and much of the text as narrated contains sweeping generalisations which would not be considered appropriate by today's standards.', '1953-01-01', '1953-12-31', '1953', 'default', '1 film 16mm Digitised', 'Running time - c.60 minutes
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/2/2/2/1/1', 'Uganda', 'King's birthday reception, Uganda', 'A ceremonial event at an unidentified location in Uganda. A union jack flag, surrounded by men in white uniforms, flies high at a reception held in honour of King George's birthday.
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/2/2/2/1/2', 'Uganda', 'Uganda exhibition for King George's coronation', 'The entrance to the Uganda Exhibition, fashioned from tied palm leaves and adorned with banners, is guarded by askaris.
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/2/2/2/1/20', 'Uganda', 'Firing Point, Entebbe', 'A group of European men, accompanied by their African servants, take practice shots from the top of a mound of earth constructed as a firing point. 
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/2/2/2/1/34', 'Uganda', 'K.A.R. Kit Inspection, Bombo', 'A body of about 30 - 40 men lined up for kit inspection are being addressed by a European officer, all have neat rolls of uniform and equipment lined up in front of them. 
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/2/2/2/1/35', 'Uganda', 'King's African Rifles, Bombo', 'A very long line of troops stretching away into the distance with rifles over their shoulders and commanders in a line to the fore. The senior officer sits astride a grey horse in the foreground. 
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/2/2/2/1/4', 'Uganda', '4th King's African Rifles Band', 'Portrait of a regiment of uniformed drummers in the 4th King's African Rifles Band, taken at an unidentified location, presumably in Uganda.
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/2/2/2/2', 'British Guiana', 'Box of negatives and prints', 'This box contained miscellaneous items from Margaret Trotter's possession when it was donated to BECM, including her 1918 diary, which was taken out and catalogued separately as 2001/090/2/3/1, and the envelopes listed here, most of them labelled in her hand.
INSERT INTO item (item_ref, location, name, description, start_date, end_date, display_date, copyrighted, extent, phys_tech_desc, multimedia_irn, subcollection_id) VALUES ('2001/090/2/3/2', 'Uganda', 'James and Margaret Trotter's documents relating to life overseas', 'This file of personal documents relates to the Trotters' life overseas in East and West Africa. It includes correspondence relating to James Trotter's war service, his "Leave and Last Pay Certificate" detailing his surveying career in Uganda and Nigeria, a passenger list for the MV Accra between the UK and Nigeria in 1931, Margaret Trotter's British-Nigerian passport, circulars including Colonial Office pensions regulations and the Torrens system of title registration in Uganda and maps of Uganda (1928) and the Ikoyi district of Lagos (1929).', '1915-01-01', '1933-12-31', '1915-1933', 'default', '1 folder', '1 folder containing typescript and printed documents. Some of the items of correspondence carry tears.', 'default', 2001/090/2);
