INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/2', 'Kenya', 'Negative books', 'These 10 notebooks, with accompanying subject index, record the details of all 50,000 images shot by Charles Trotter during his active years as a photographer in Kenya, and catalogued at 2001/090/1/1. The images are listed by negative number (following a chronological order), with the date, brief content and size recorded. The subject index contains details of the majority of Trotter\'s commissions arranged by theme.', '1948-1962', 'default', '1 box', 'default', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/2/1', 'Kenya', 'Negative book 1', 'Copy of missing school exercise book containing details of number, date, subject and size of negatives 1-2104.', 'February 1948 - 21 June 1952', 'default', '1 document', '1 A4 photocopied document, containing a bundle of pages tied together with archival tape.', 3);
