INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/2/11', 'Kenya', 'Negative subject index', 'Subject index to his c. 50,000 negatives kept by Trotter during his photographic career. The negative numbers, dates and brief description of each commission/ sequence of images are recorded under the following subjects: Air Photos & Aircraft, Buildings, towns, Commercial, Dogs & Domestic Pets, Babies & Kids, Social Events, Horses, Horse Shows, Stage Shows, Film Stills, Film Stars, Sport, Motoring Events, Accidents, Portraits & People, Parades, Royal Visits, Visiting celebrities, Natives, Scenery, Views, Landscapes, Game Animals, Ships, Weddings, Agricultural Shows, Miscellaneous.', '1951-1962', 'default', '1 volume', '1 A4 hard-backed lined alphabetical index book, with entries made in ink. The spine is broken and the front and back covers are almost completely detached.', 3);
