INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/18', 'Kenya', 'Press gathered at Government House', 'Press photographers and journalists, both European and Asian, gather at a garden party at Government House, Nairobi, to take pictures of the Royal party.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/19', 'Kenya', 'Lady Macmillan is presented to Princess Elizabeth', 'Princess Elizabeth and The Duke of Edinburgh are speaking to Lady Lucie McMillan, widow of Lord McMillan.  Behind them is a large group of guests of some 40 people watching the proceedings, many wearing uniforms.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/2', 'Kenya', 'Preparing for the Royal visit', 'One European AA officer with beret attaching a sign to a hedge while another, African, officer with a fez holding an additional sign.  A Landrover marked \'Road Service\' is parked in the background.', '[January] 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/20', 'Kenya', 'Garden party at Government House', 'Princess Elizabeth and The Duke of Edinburgh are formally greeted by an Asian man who is shaking the Princess\'s hand.  Beside him stands an Asian woman wearing traditional dress.  Behind the Princess and Duke is a sizeable crowd of onlookers, some with uniforms and some in civilian dress.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/21', 'Kenya', 'The deeds of the Royal Lodge are presented', 'default', 'February 1952', 'default', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/22', 'Kenya', 'Princess Elizabeth with the Governor of Kenya', 'Princess Elizabeth, facing the camera, walks ahead of the Govenor of Kenya Sir Philip Mitchell at a garden party at Government House, Nairobi. Photographers are standing to her right in the background.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
