INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/25', 'Kenya', 'The K.A.R. Band at Government House', 'A procession of uniformed African soldiers from the King\'s African Rifles walk in procession through the garden at Government House, Nairobi.  At the front of the procession are drummers, followed by cymbalists and a large brass section.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/26', 'Kenya', 'Royal party listening to the K.A.R. Band', 'Princess Elizabeth, the Duke of Edinburgh and a large crowd of spectators including Lady Mitchell and three uniformed officers stand on the steps in front of Government House, Nairobi.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/27', 'Kenya', 'Asian schoolchildren arrive at Government House', 'A group of Asian teenagers, dressed in school uniform, are escorted by adults as they march through a field with trees in the background.  Many of the congregation are carrying Union Jack flags.  They are carrying a banner which reads \'We Reaffirm Our Loyalty to the Crown Govt. Asian High School.\'\'', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/28', 'Kenya', 'Asian schoolchildren face the camera', 'A large group of Asian children and teenagers stand facing the camera.  Some of the children wear turbans and official school uniforms.  They are escorted by adult teachers. Both children and teachers carry Union jack flags, and the children carry a banner which reads: \'Loyal Greetings From (obscured) High School.\'\'', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/29', 'Kenya', 'Convent schoolchildren at Government House', 'A large line of European school girls walk through a field with trees in the background.  The girls all wear white dresses with dark blazers and round hats.   On the left flank of the line march a small group of African school girls wearing white dresses.  In the background of the picture are a group of onlookers; some stand and others sit on a truck.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/3', 'Kenya', 'Construction of the Princess Elizabeth Highway', 'A group of construction workers stand in the foreground with a lorry marked \'East African Enterprise Ltd.\'  The newly constructed road stretches ahead with banners lining the sides and Union Jack bunting overhead.', '[January] 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
