INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/31', 'Kenya', 'Royal party leaving Government House', 'From underneath the white pillars of Government House, Nairobi, Princess Elizabeth and the Duke of Edinburgh walk away from the reception.  Various uniformed guards stand to attention.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
