INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/33', 'Kenya', 'Royal party followed by the Governor of Kenya', 'Princess Elizabeth and the Duke of Edinburgh walk out from Government House along a red carpet. Behind them is the Govenor of Kenya followed by two other unidentified men and a woman.  A small group of onlookers observe from the window of Government House.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/34', 'Kenya', 'Leaving by limousine from Government House', 'Princess Elizabeth and the Duke of Edinburgh are about to climb into a waiting limousine outside Government House.  There is an African chauffeur in the driving seat.  In the background stand a party of children waving Union Jack flags.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/35', 'Kenya', 'Royal party driving past schoolchildren', 'The Royal Limousine is pictured in the foreground and blurred by motion, driving past a large group of schoolchildren all waving Union Jack flags.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
