INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/41', 'Kenya', 'Nurses outside the King George VI Hospital, Nairobi', 'Outside the main entrance of the hospital stand a group of African nurses on either side of the entrance.  Each group is being addressed by a European sister.  All of the nurses wear white head scarfs.  In the centre of the entrance stand a number of European men in suits, possibly hospital officials.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
