INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/47', 'Kenya', 'Duke of Edinburgh with Matron, King George VI Hospital', 'The Duke of Edinburgh, in profile, laughs with a hospital matron during his tour of the hospital.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/48', 'Kenya', 'Duke of Edinburgh arrives at Kenya Regiment HQ', 'The Duke of Edinburgh emerges from a Rolls Royce car and amidst bunting lined streets, greets a group of uniformed army officers.  Outside of an official building, a group of people in civilian dress stand to greet the Royal guest.  African soldiers stand in front of the onlookers who are separated by a rope boundary.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/49', 'Kenya', 'Duke of Edinburgh with Officers of the Kenya Regiment', 'The Duke of Edinburgh stands with a group of four army officers. On the left of the picture stands a younger officer who stands to attention.  Onlookers stand in the background, they are mainly European and some are taking photographs.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
