INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/57', 'Kenya', 'Princess Elizabeth is driven past onlookers', 'An open topped car speeds past the camera carrying Princess Elizabeth and the Duke of Edinburgh to Nairobi\'s City Hall.  The Duke of Edinburgh is partially obscured by the window of the car but can be seen waving his left hand.  Princess Elizabeth is seen smiling to the crowds of people who line the road.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/58', 'Kenya', 'Nairobi City Hall', 'Front and side elevations of Nairobi\'s City Hall. Outside the building are a line of flags bearing the Union Jack together with bunting and pennants depicting an ostrich with the words "CITY OF NAIROBI.\'\'  One flag bears the cross of St. Andrew.  Workmen can be seen erecting a flag at the front of the City Hall. and cars line the street outside.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
