INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/6', 'Kenya', 'African Chiefs await the Royal party at Eastleigh Airport', 'Photograph depicting five elderly chiefs sitting at the front of a spectator crowd awaiting the arrival of the Royal party.  The man sitting closest to camera wears a head scarf and holds a staff bearing the Royal insignia.  Two other men also wear this head dress.  The men sitting in the middle of the front row wear feathered plumes and leopard skin neck ruffs.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
