INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/61', 'Kenya', 'Mayor and Mayoress of Nairobi await the Royal party', 'Standing on the front steps of Nairobi City Hall are the Mayor and Mayoress of Nairobi.  The mayor wears a chain of office and holds a hat in his left hand.  Above them can be seen part of the motto "CONSILIO FIDE VIGILANTIA."', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/62', 'Kenya', 'default', 'default', 'Feb-52', 'default', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/63', 'Kenya', 'On the balcony of Nairobi City Hall', 'From the balcony of Nairobi City Hall, the Princess and the Duke wave to the crowds.  They stand between two of the building\'s four frontal pillars and below them is a large pennant with two lions either side of a coat of arms.  Below the coat of arms is the motto "CONSILIO FIDE VIGILANTIA.\'\'  In front of the building flies a flag bearing the Royal crest.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/64', 'Kenya', 'Close up of Princess and Duke on the balcony', 'Detail from previous photograph (2001/090/1/4/1/63, negative number 1319) showing a close-up image of the Princess and the Duke waving to the crowds from the balcony of Nairobi City Hall.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/65', 'Kenya', 'Royal party leaving City Hall', 'Princess Elizabeth and the Duke of Edinburgh, together with the Mayor and Mayoress of Nairobi, stand on the steps of City Hall as they get ready to leave.  The Princess waves as the whole group looks to its right.  In front of the group stands a mace bearer wearing a military uniform.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/66', 'Kenya', 'New Stanley Hotel, Nairobi', 'Photograph showing the front elevation of the New Stanley Hotel in Nairobi, shortly before its 1950s rebuilding.  Outside of the hotel is a busy street scene, with bunting and a Union Jack flag.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
