INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/68', 'Kenya', 'Sagana Lodge, Nyeri', 'View across gardens of Sagana Lodge, the "Royal Lodge" presented to Princess Elizabeth who according to Trotter\'s caption left Kenya without visiting it, although she is believed to in fact have spent one night there. The photograph shows a ground floor timber building with a tiled roof set amongst trees and copious flower boundaries.  A gravel circular drive curves in front of the building.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
