INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/7', 'Kenya', 'African chiefs wait behind guard, Eastleigh Airport', 'A crowd of African male spectators with front most spectators wearing tribal dress sit behind a rope line waiting for the arrival of the Royal party.  In front of this stands an official guard who, standing to attention, wears a fez.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/70', 'Kenya', 'Sagana Lodge and grounds', 'Rear elevation of Sagana Lodge, a ground floor timber building on a stone basement with a tiled roof set amongst trees and copious flower boundaries.  In the foreground is a garden with unidentified plants growing in it.  A secondary, smaller building is set back from the main lodge.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
