INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/73', 'Kenya', 'River running through the grounds of Sagana Lodge', 'View down from a grassy bank onto a river running through the grounds of the Sagana (Royal) Lodge.  On the right hand side it is bordered by a small path and a more indistinguishable track on the other side of the river.  Lots of trees surround.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
