INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/76', 'Kenya', 'Guard looking over the gardens of Sagana Lodge', 'An African guard looking out to the garden that adjoins the Sagana (Royal) Lodge.   He is standing on a grassy bank overlooking a stream / river than runs through the garden.  The area is heavily wooded.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/77', 'Kenya', 'Press conference at Sagana Lodge to announce the death of King George VI', 'A group of nine people in a room of the Sagana "Royal" Lodge who are engaged in writing down the news of the death of King George VI.   A man who is standing appears to address the group.  The Royal couple had been due to spend a holiday at the Lodge but left folllowing news of the King\'s death.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
