INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/79', 'Kenya', 'Memorial service to King George VI, Nairobi Cathedral', 'Exterior view of Nairobi Cathedral, taken at a distance from the cathedral and showing a large number of people, possibly schoolchildren, seated on chairs in the grounds. There are about 20 cars parked in the  foreground.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/8', 'Kenya', 'Governor of Kenya, Sir Phillip Mitchell', 'The Govenor of Kenya, wearing a helmet with a plume of feathers, greets an African elder dressed in a leopard skin neck ruff and a plume of feathers on his head.  Surrounding them are other African chiefs awaiting the arrival of the royal party.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/80', 'Kenya', 'Schoolchildren outside Nairobi Cathedral', 'The photograph has been taken at a distance in front of the cathedral and shows a large group of school children with heads bowed sitting in front of the cathedral\'s main entrance. The flag of St George is at half mast at the top of the left hand tower.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
INSERT INTO item (item_ref, location, name, description, date_created, copyrighted, extent, phys_tech_desc, subcollection_id) VALUES ('2001/090/1/4/1/81', 'Kenya', 'Mourners outside Nairobi Cathedral', 'This photograph has been taken at a distance from the cathedral and shows a large number of people seated on rows of chairs on several sides of the building. Various onlookers (European, Asian and African) stand near a wall in the foreground.', 'February 1952', '[Copyright] Bristol Record Office', '1 photograph', '1 b/w photograph, glued into album.', 3);
